require(['jquery', 'xwiki-events-bridge'], function ($) {
  /*
   * Inject a button for user import in the Users and Groups administration sections.
   */
  var importUsersButton = $('#importUsersButton');
  var importUsersSection = $('#groupEditImportUserSection');

  /*
   * Append the Import User button in several needed places.
   * 1. In the Users administration section, next to Create User button, in the main wiki.
   * 2. In the Users administration section, next to Add and Invite buttons, in any subwiki.
   */
  $.each([
    $("button[data-target='#createUserModal']"),
    $('#inviteUserButton')],
    function() {
      // The same element cannot be appended in different places.
      var importUsersButtonClone = importUsersButton.clone();
      importUsersButtonClone.removeClass('hidden');
      $(this).parent().append(importUsersButtonClone);
    }
  );
  /*
   * Append the Import User button in several needed places.
   * 1. In any Group page, edit mode, splitting the screen in 2, sharing width with Add members form.
   * 2. In any Group's pop-up edit modal, splitting the screen in 2, sharing width with Add members form.
   */

  var updateAddMembersForm = function() {
    var editForm = $('#addMembers').closest('.xform').not('#inviteUserForm');
    if (editForm.size() > 0) {
      editForm.wrap("<div class='row'></div>").addClass('col-sm-6');
      var importUsersSectionClone = importUsersSection.clone();
      importUsersSectionClone.removeClass('hidden');
      // The same element cannot be appended in different places.
      var importUsersButtonClone = importUsersButton.clone();
      importUsersButtonClone.removeClass('hidden');
      importUsersSectionClone.append(importUsersButtonClone);
      editForm.parent().append(importUsersSectionClone);
    }
  };

  updateAddMembersForm();

  // Update Add members form in edit group modal;
  $(document).on('xwiki:dom:updated', function() {
    updateAddMembersForm();
  });
  // Hide the Edit Group modal, when using the Import Users feature.
  $('#editGroupModal').on('click', '#importUsersButton', function() {
    $('#editGroupModal').modal('hide');
  });
});

require(['jquery'], function ($) {
  var isInEditGroupModal = false;
  // Set the groupReference value when accessing the edit group modal.
  $('#groupstable').on('click', '.actionedit', function() {
    var groupReference = $(this).closest('tr').find('.group').data('reference');
    $("#importUsersModal input[name='groupReference']").val(groupReference);
    isInEditGroupModal = true;
  });
  var resultsContainer = $('#searchResults');
  var searchLoading = $('#searchResultsLoading');
  var importLoading = $('#importResultsLoading');
  var importButton = $('#importButton');
  // Get user results from LDAP.
  $('#searchLDAPUsers').submit(function(event) {
    event.preventDefault();
    var searchLDAPUsersForm = $(this);
    $('#ldapSearchBox').removeClass('hidden');
    searchLoading.removeClass('hidden');
    resultsContainer.addClass('hidden');
    // Make sure the message is hidden all the time even if the form is submitted without reloading the page.
    $('#ldapSearchBox .infomessage').addClass('hidden');
    $('#ldapSearchBox .ldapError').addClass('hidden');
    // The import button should be displayed in relation with the results, thus it should be hidden by default for each
    // search operation.
    importButton.addClass('hidden');
    var displayImportButton = false;
    $.post(searchLDAPUsersForm.attr('action'), searchLDAPUsersForm.serialize())
     .done(function(data) {
      var users = data.users;
      if (data.displayedMax) {
        $('#ldapSearchBox .infomessage').removeClass('hidden');
      }
      searchLoading.addClass('hidden');
      resultsContainer.removeClass('hidden');
      if ($.isEmptyObject(users)) {
        resultsContainer.html(data.noResults);
      } else {
        var resultsList = $('<ul/>').attr({'id': 'usersList'});
        $.each(users, function(index, value) {
          var tooltipInfo = '';
          $.each(value, function(i, v) {
            if (i !== 'displayMessage')
            tooltipInfo += i + ' = ' + v + '\n';
          })
          var displayedText = value['displayMessage'] + ' ';
          var itemCSSClass = 'notImported';
          var listItem = $('<li/>');
          // If the user exists, it will be represented as plain text.
          // If the user doesn't exist, it will be represented as a label and a checkbox to be selected for import.
          if (value.exists === 'true') {
            var userProfile = $('<a/>').attr({'href': value['userProfileURL']}).text(value['userProfile']);
            listItem.html(displayedText);
            listItem.append(userProfile);
            itemCSSClass = 'imported';
          } else {
            displayImportButton = true;
            var checkbox = $('<input/>').attr({
              'type': 'checkbox',
              'name': 'user',
              'id': index,
              'value': index
            });
            var label = $('<label/>').attr({
              'for': index,
              'title': tooltipInfo
            }).text(displayedText + value['userProfile']);
            listItem.append(checkbox);
            listItem.append(label);
          }
          listItem.attr({'class': itemCSSClass});
          resultsList.append(listItem);
        });
        resultsContainer.html(resultsList);
        // Show the Import button, after search was performed with results.
        if (displayImportButton) {
          importButton.removeClass('hidden');
        }
      }
    }).fail(function(data) {
      searchLoading.addClass('hidden');
      $('#ldapSearchBox .ldapError').removeClass('hidden').html(data.responseText);
    });
  });

  // Perform the user import.
  $('#importLDAPUsers').submit(function(event) {
    event.preventDefault();
    importLoading.removeClass('hidden');
    resultsContainer.addClass('hidden');
    var importLDAPUsersForm = $(this);
    importButton.prop('disabled', true);
    // Make sure the message is hidden all the time even if the form is submitted without reloading the page.
    $('#ldapSearchBox .infomessage').addClass('hidden');
    $.post(importLDAPUsersForm.attr('action'), importLDAPUsersForm.serialize())
     .done(function(data) {
      importLoading.addClass('hidden');
      resultsContainer.removeClass('hidden');
      var users = data.users;
      if ($.isEmptyObject(users)) {
        resultsContainer.html(data.noResults);
      } else {
        var resultsList = $('<ul/>').attr({'id': 'importedUsersList'});
        $.each(users, function(index, value) {
          var displayedText = value['displayMessage'] + ' ';
          var userProfile = $('<a/>').attr({'href': value['userProfileURL']}).text(value['userProfile']);
          var listItem = $('<li/>');
          listItem.html(displayedText);
          listItem.append(userProfile);
          resultsList.append(listItem);
          resultsContainer.html(resultsList);
        });
        // Hide the Import button, after import was performed.
        importButton.addClass('hidden');

        // Make sure to update the current livetable to see new users.
        $('#importUsersModal').on('click', "[data-dismiss='modal']", function() {
          var livetable = window['livetable_' + $('.xwiki-livetable').attr('id')];
          if ($.type(livetable) !== 'undefined') {
            livetable.refresh();
          } else {
            // For the Group page.
            editgrouptable.refresh();
          }
          // Display back the edit group modal, if exists.
          if (isInEditGroupModal === true) {
            $('#editGroupModal').modal('show');
          }
        });
        importButton.prop('disabled', false);
      }
    }).fail(function(data) {
      $('#importResultsLoading').addClass('hidden');
      $('#ldapSearchBox .ldapError').removeClass('hidden').html(data.responseText);
      importButton.prop('disabled', false);
    });
  });
  $('.modal').on('click', '.xwikirenderingerror', function() {
    $(this).siblings('.xwikirenderingerrordescription').toggleClass('hidden');
  });
});

require(['jquery'], function($) {
  var serviceURL = new XWiki.Document('LDAPUserImportService', 'LDAPUserImport').getURL('get');
  var groupsTable = $('#groupstable');
  var formToken = $('html').data('xwiki-form-token');
  var searchLoading = $('#groupSearchResultsLoading');
  var confirmGroupUpdateButton = $('#confirmGroupUpdate');
  var groupMembersSizeLoading = $('#groupMembersSizeLoading');
  var groupUpdateLoading = $('#groupUpdateLoading');
  var updateStatus = $('#updateGroupModal .modal-body #updateGroupStatus');

  var addAssociateGroupsButtons = function() {
    if (groupsTable.length > 0) {
      var associateGroupsButton = $('.actionassociategroups');
      $.each($('#groupstable-display tr td.actions'), function(index, value) {
        if ($(this).find('.actionassociategroups').length === 0) {
          var associateGroupsButtonClone = associateGroupsButton.clone();
          associateGroupsButtonClone.removeClass('hidden');
          $(this).append(associateGroupsButtonClone);
        }
      });
    }
  };

  var addUpdateGroupButtons = function() {
    if (groupsTable.length > 0) {
      // Get the XWiki groups mapped to LDAP groups.
      $.post(serviceURL,
        {
          'outputSyntax': 'plain',
          'action': 'getMappedXWikiGroups',
          'form_token': formToken
        }
      ).done(function(data) {
        var ldapUpdateGroupButton = $('.actionupdategroup');
        $.each(data.mappedXWikiGroups, function(index, groupName) {
          var ldapUpdateGroupButtonClone = ldapUpdateGroupButton.clone();
          ldapUpdateGroupButtonClone.removeClass('hidden');
          ldapUpdateGroupButtonClone.attr({'data-xWikiGroupName': groupName})
          var groupNameCell = groupsTable.find(".group[data-reference$='" + groupName + "']").parent();
          var groupActionsCell = groupNameCell.siblings('td.actions');
          if (groupActionsCell.find('.actionupdategroup').length === 0) {
            groupActionsCell.append(ldapUpdateGroupButtonClone);
          }
        });
      });
    }
  };
  addAssociateGroupsButtons();
  $(document).on('xwiki:livetable:displayComplete', addAssociateGroupsButtons);
  addUpdateGroupButtons();
  $(document).on('xwiki:livetable:displayComplete', addUpdateGroupButtons);

  // Trigger the import/group update.
  groupsTable.on('click', '.actionupdategroup', function(event) {
    var xWikiGroupName = $(this).data('xwikigroupname');
    // Make sure the status is always clean, even when updating multiple groups without reloading the page.
    updateStatus.html('');
    // Hide the confirmation button by default and make it visibile when there are users to update.
    confirmGroupUpdateButton.addClass('hidden');
    $('#updateGroupModal .ldapError').addClass('hidden')
    confirmGroupUpdateButton.attr({'data-xWikiGroupName': xWikiGroupName});
    groupMembersSizeLoading.removeClass('hidden');
    $.post(serviceURL,
      {
        'outputSyntax': 'plain',
        'action': 'getGroupMemberSize',
        'xWikiGroupName': xWikiGroupName,
        'form_token': formToken
      }
    ).done(function(data) {
      groupMembersSizeLoading.addClass('hidden');
      var groupMemberSize = data.groupMemberSize;
      updateStatus.html(data.groupMemberSizeInfo);
      if (data.groupMemberSize > 0) {
        confirmGroupUpdateButton.removeClass('hidden');
      }
    }).fail(function(data) {
      groupMembersSizeLoading.addClass('hidden');
      $('#updateGroupModal .ldapError').removeClass('hidden').html(data.responseText);
    });
  });
  // Trigger the group update.
  confirmGroupUpdateButton.click(function() {
    groupMembersSizeLoading.addClass('hidden');
    groupUpdateLoading.removeClass('hidden');
    var xWikiGroupName = $(this).data('xwikigroupname');
    // Make sure the status is always clean, even when updating multiple groups without reloading the page.
    updateStatus.html('');
    $.post(serviceURL,
      {
        'outputSyntax': 'plain',
        'action': 'updateGroup',
        'xWikiGroupName': xWikiGroupName,
        'form_token': formToken
      }
    ).done(function(data) {
      groupUpdateLoading.addClass('hidden');
      confirmGroupUpdateButton.addClass('hidden');
      updateStatus.html(data.message);
    }).fail(function(data) {
      groupUpdateLoading.addClass('hidden');
      $('#updateGroupModal .ldapError').removeClass('hidden').html(data.responseText);
    });
  });
});

require(['jquery'], function ($) {
  var groupsTable = $('#groupstable');
  var resultsContainer = $('#groupSearchResults');
  var searchLoading = $('#groupSearchResultsLoading');
  var associateResultsLoading = $('#associateResultsLoading');
  var associateButton = $('#associateButton');

  // Set the XWiki group name in the associateLDAPGroups form
  $('#groupstable').on('click', '.actionassociategroups', function() {
    var xWikiGroupName = $(this).closest('tr').find('.name .group').data('reference');
    var localXWikiGroupName = xWikiGroupName.split(':')[1];
    $('#searchLDAPGroups').find('input[name=xWikiGroupName]').val(localXWikiGroupName);
    $('#associateLDAPGroups').find('input[name=xWikiGroupName]').val(localXWikiGroupName);
  });

  // Get group results from LDAP.
  $('#searchLDAPGroups').submit(function(event) {
    event.preventDefault();
    var searchLDAPGroupsForm = $(this);
    $('#ldapGroupSearchBox').removeClass('hidden');
    searchLoading.removeClass('hidden');
    resultsContainer.addClass('hidden');
    // Make sure the message is hidden all the time even if the form is submitted without reloading the page.
    $('#ldapGroupSearchBox .infomessage').addClass('hidden');
    $('#ldapGroupSearchBox .ldapError').addClass('hidden');
    // The group associate button should be displayed in relation with the results, thus it should be hidden by default for each
    // search operation.
    associateButton.addClass('hidden');
    var displayAssociateButton = false;
    $.post(searchLDAPGroupsForm.attr('action'), searchLDAPGroupsForm.serialize())
     .done(function(data) {
      var groups = data.groups;
      if (data.displayedMax) {
        $('#ldapGroupSearchBox .infomessage').removeClass('hidden');
      }
      searchLoading.addClass('hidden');
      resultsContainer.removeClass('hidden');
      if ($.isEmptyObject(groups)) {
        resultsContainer.html(data.noResults);
      } else {
        var resultsList = $('<ul/>').attr({'id': 'groupsList'});
        $.each(groups, function(index, value) {
          var tooltipInfo = '';
          $.each(value, function(i, v) {
            if (i !== 'displayMessage')
            tooltipInfo += i + ' = ' + v + '\n';
          })
          var displayedText = value['displayMessage'] + ' ';
          var itemCSSClass = 'notAssociated';
          var listItem = $('<li/>');
          // If the group is associated, it will be represented as plain text.
          // If the group is not associated, it will be represented as a label and a checkbox to be selected for association.
          if (value.isAssociated === 'true') {
            listItem.html(displayedText);
            itemCSSClass = 'associated';
          } else {
            let escapedValue = value.dn.replaceAll('\\', '\\\\');
            displayAssociateButton = true;
            var checkbox = $('<input/>').attr({
              'type': 'checkbox',
              'name': 'group',
              'id': index,
              'value': escapedValue
            });
            var label = $('<label/>').attr({
              'for': index,
              'title': tooltipInfo
            }).text(displayedText );
            listItem.append(checkbox);
            listItem.append(label);
          }
          listItem.attr({'class': itemCSSClass});
          resultsList.append(listItem);
        });
        resultsContainer.html(resultsList);
        // Show the Associate button, after search was performed with results.
        if (displayAssociateButton) {
          associateButton.removeClass('hidden');
        }
      }
    }).fail(function(data) {
      searchLoading.addClass('hidden');
      $('#ldapGroupSearchBox .ldapError').removeClass('hidden').html(data.responseText);
    });
  });

  // Perform the groups association.
  $('#associateLDAPGroups').submit(function(event) {
    event.preventDefault();
    associateResultsLoading.removeClass('hidden');
    resultsContainer.addClass('hidden');
    var associateLDAPGroupsForm = $(this);
    associateButton.prop('disabled', true);
    var xWikiGroupName = associateLDAPGroupsForm.find('input[name=xWikiGroupName]').val();
    // Make sure the message is hidden all the time even if the form is submitted without reloading the page.
    $('#ldapGroupSearchBox .infomessage').addClass('hidden');
    $('#ldapGroupSearchBox .ldapError').addClass('hidden');
    $.post(associateLDAPGroupsForm.attr('action'), associateLDAPGroupsForm.serialize())
     .done(function(data) {
      associateResultsLoading.addClass('hidden');
      resultsContainer.removeClass('hidden');
      associateButton.addClass('hidden');
      resultsContainer.html(data.message);
      if (data.status === 'success') {
        associateButton.prop('disabled', false);
      }
    }).fail(function(data) {
      associateResultsLoading.addClass('hidden');
      $('#ldapGroupSearchBox .ldapError').removeClass('hidden').html(data.responseText);
    });
  });
  // Make sure to update the current livetable to see the updates.
  $.each([$('#updateGroupModal'), $('#associateGroupsModal')], function() {
    $(this).on('click', "[data-dismiss='modal']", function() {
      var livetable = window['livetable_' + $('.xwiki-livetable').attr('id')];
      if ($.type(livetable) !== 'undefined') {
        livetable.refresh();
      } else {
        location.reload();
      }
    });
  });
});

